function fizzbuzz(n) {

    var arr = [];

    for (var i = 0; i <= n; i++) {
        if (i % 3 == 0 && i % 5 == 0) {
            arr.push('FizzBuzz');
        } else if (i % 3 == 0) {
            arr.push('Fizz');
        } else if (i % 5 == 0) {
            arr.push('Buzz');
        } else {
            arr.push(i);
        }
    }
    function fizzify(x) {

        var num = Math.floor(Math.random(arr) * n)

        return num;
    }
    return fizzify(arr);
}
