function addPrimes(num) {

    var primes = [], sum = 0;

    for (var i = 0; i <= num; i++) {
		var isPrime = true;
		for (var j = 2; j <= i; j++) {
			if (i % j == 0 && j != i) {
				Prime = false;
			}
		}
		if (isPrime == true) {
			primes.push(i);
		}
	}
	for (var x = 0; x <= primes.length; x++) {
		sum += primes[x];
	}
	return sum;
}
